tests.testString:
while (playerScore < 3 && computerScore < 3) {
  showResults("Rock");
}

if (playerScore === 3) {
  assert.equal(winnerMsgElement.innerText, "Player has won the game!");
} else {
  assert.equal(winnerMsgElement.innerText, "Computer has won the game!");
}
