        const transformedCode = code.replace(/\r/g, "");
        const if_statement = __helpers.python.getBlock("\n" + transformedCode, "if number >= 10");
        const {block_body} = if_statement;

        assert.notMatch(block_body, /sum_of_even_digits *\+= *number/);
    }
})
